import streamlit as st

st.title("Chai Taste Poll")

col1,col2,col3,col4= st.columns(4)

with col1:
    st.header("MASALA Chai")
    st.image("data:image/jpeg;base64,", width=200)
    vote1=st.button("Vote for Masala Chai")

with col2:
    st.header("LEMON Chai")
    st.image("data:image/jpeg;base64,", width=200)
    vote2=st.button("Vote for Lemon Chai")
    
with col3:
    st.header("CUTTING Chai")
    st.image("data:image/jpeg;base64,")
    vote3=st.button("Vote for Garlic Chai")
    
with col4:
    st.header("ADRAK Chai")
    st.image("data:image/jpeg;base64,")
    vote4=st.button("Vote for Adrak Chai")
    
st.write("---")
if vote1:
    st.success("Thanks for voting for Masala Chai")
    
elif vote2:
    st.success("Thanks for voting for Lemon Chai")
    
elif vote3:
    st.success("Thanks for voting for Cutting Chai")
    
elif vote4:
    st.success("Thanks for voting for Adrak Chai")
    
name=st.sidebar.text_input("Enter your name")

tea=st.sidebar.selectbox(
    "Select your favorite tea",
    ["Masala Chai", "Lemon Chai", "Cutting Chai", "Adrak Chai"]
)
st.write(f"Welcome {name} to the Chai Taste Poll and your favorite chai {tea} is getting ready!")
if name and tea:
    st.write(f"Thanks for voting for {tea} by {name}")